import requests
import pandas as pd
from bs4 import BeautifulSoup
from openpyxl import Workbook
import time

# 엑셀 파일에서 데이터 읽기
excel_file = "레볼루션2게임리스트-2.xlsx"
df = pd.read_excel(excel_file)

# 컬럼명 (사용자가 제공한 컬럼명 리스트)
columns = [
    "어드밴스마메", "마메", "아토미스웨이브", "FB네오", "MSX2", "PC엔진",
    "닌텐도64", "닌텐도DS", "닌텐도엔터테이먼트시스템", "닌텐도게임보이어드밴스",
    "슈퍼닌텐도엔터테이먼트시스템", "드림캐스트", "세가마스터시스템", "세가메가드라이브",
    "세가나오미", "네오지오", "플레이스테이션", "PSP"
]

# 모든 컬럼의 데이터를 리스트로 추출 (NaN 제거)
game_titles = []
for col in columns:
    if col in df:
        game_titles.extend(df[col].dropna().astype(str).tolist())

# 검색어 리스트에서 모든 공백 제거
game_titles = [title.replace(" ", "") for title in game_titles]

# 요청 URL
url = "https://www.grac.or.kr/Statistics/GameStatistics.aspx?gameTitle="

# 요청 헤더
headers = {
    "accept": "text/html,application/xhtml+xml,application/xml;q=0.9,image/avif,image/webp,image/apng,*/*;q=0.8,application/signed-exchange;v=b3;q=0.7",
    "accept-encoding": "gzip, deflate, br, zstd",
    "accept-language": "ko-KR,ko;q=0.9,en-US;q=0.8,en;q=0.7",
    "cache-control": "max-age=0",
    "connection": "keep-alive",
    "content-length": "30902",
    "content-type": "multipart/form-data; boundary=----WebKitFormBoundaryT9WByK5XUbyAlsOk",
    "cookie": "ASP.NET_SessionId=erjcmrim3mljquzrae0yjmog; VisitsStatistics=2025-03-02; _fwb=233bNjC65Jfnutdl6gsteqN.1740914771613; wcs_bt=200c48bd004a060:1740920690",
    "host": "www.grac.or.kr",
    "origin": "https://www.grac.or.kr",
    "referer": "https://www.grac.or.kr/Statistics/GameStatistics.aspx?gameTitle=",
    "sec-ch-ua": '"Not(A:Brand";v="99", "Google Chrome";v="133", "Chromium";v="133")',
    "sec-ch-ua-mobile": "?0",
    "sec-ch-ua-platform": '"Windows"',
    "sec-fetch-dest": "document",
    "sec-fetch-mode": "navigate",
    "sec-fetch-site": "same-origin",
    "sec-fetch-user": "?1",
    "upgrade-insecure-requests": "1",
    "user-agent": "Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/133.0.0.0 Safari/537.36"
}
# 수집된 데이터를 저장할 리스트
game_data = []

# 게임 제목별로 요청 보내기
for index, title in enumerate(game_titles, start=1):
    try:
        print(f"[{index}/{len(game_titles)}] Processing: {title}")

        # POST 요청 페이로드
        payload = {
            "_EVENTTARGET": "ctl00$ContentHolder$lbtnSearch",
            "_EVENTARGUMENT": "",
            "_VIEWSTATE": "",
            "ctl00$totalSearch": "",
            "ctl00$ContentHolder$hdnScreenWidth": "1027",
            "ctl00$ContentHolder$ddlOrganization": "",
            "ctl00$ContentHolder$ddlApplicationGroup": "",
            "ctl00$ContentHolder$ddlPlatForm": "",
            "ctl00$ContentHolder$ddlRate": "",
            "ctl00$ContentHolder$txtRatingNbr": "",
            "ctl00$ContentHolder$txtGameTitle": title,
            "ctl00$ContentHolder$txtEntName": "",
            "ctl00$ContentHolder$txtStartDate": "",
            "ctl00$ContentHolder$txtEndDate": "",
            "ctl00$ContentHolder$ajxMask1_ClientState": "",
            "ctl00$ContentHolder$ajxMask2_ClientState": "",
            "ctl00$ContentHolder$hdnMenualDownMode": "",
            "ctl00$ContentHolder$Evaluation$rbSatisfy4": "rbSatisfy4",
            "ctl00$ContentHolder$Evaluation$taContents": "",
            "hiddenInputToUpdateATBuffer_CommonToolkitScripts": "1",
        }

        # 요청 보내기 (예외 처리)
        try:
            response = requests.post(url, headers=headers, data=payload, timeout=10)
            response.raise_for_status()  # HTTP 에러 발생 시 예외 처리
        except requests.exceptions.RequestException as e:
            print(f"요청 오류 발생: {e} (게임: {title})")
            continue  # 다음 게임으로 넘어감

        # 응답 HTML 파싱 (예외 처리)
        try:
            soup = BeautifulSoup(response.text, "html.parser")
        except Exception as e:
            print(f"HTML 파싱 오류 발생: {e} (게임: {title})")
            continue

        # 테이블 찾기
        table = soup.find("table", class_="board list fixed mt10 statistics")
        if not table:
            print(f"No data found for {title}")
            continue

        tbody = table.find("tbody")
        rows = tbody.find_all("tr") if tbody else []

        # 각 행에서 데이터 추출 (예외 처리)
        for row in rows:
            try:
                columns = row.find_all("td")
                if len(columns) < 12:
                    continue

                game_info = {
                    "Index": index,  # 인덱스 추가
                    "게임물명": columns[0].get_text(strip=True),
                    "신청자": columns[1].get_text(strip=True),
                    "분류일자": columns[2].get_text(strip=True),
                    "등급": columns[3].get_text(strip=True),
                    "분류번호": columns[4].get_text(strip=True),
                    "취소": columns[5].get_text(strip=True),
                    "결정일": columns[6].get_text(strip=True),
                    "설명서": columns[7].get_text(strip=True),
                    "기관": columns[8].get_text(strip=True),
                    "결정내용": columns[9].get_text(strip=True),
                    "등급이력": columns[10].get_text(strip=True),
                    "해외등급": columns[11].get_text(strip=True),
                }

                game_data.append(game_info)

            except Exception as e:
                print(f"데이터 추출 오류 발생: {e} (게임: {title})")
                continue  # 다음 행으로 넘어감

        # 요청 간 1초 대기 (서버 부담 방지)
        time.sleep(1)

    except Exception as e:
        print(f"알 수 없는 오류 발생: {e} (게임: {title})")
        continue  # 다음 게임으로 넘어감

# 데이터프레임 생성
output_df = pd.DataFrame(game_data)

# 엑셀로 저장
output_file = "게임등급_결과.xlsx"
output_df.to_excel(output_file, index=False)

print(f"데이터 저장 완료: {output_file}")
