def sanitize_folder_name(folder_name):
    """í´ë” ì´ë¦„ì—ì„œ Windows ê¸ˆì§€ ë¬¸ìë¥¼ ì œê±°í•©ë‹ˆë‹¤."""
    return re.sub(r'[\\/:*?"<>|]', '_', folder_name)


def download_with_retry(url, save_path, retries=3, delay=2):
    """ì´ë¯¸ì§€ ë‹¤ìš´ë¡œë“œë¥¼ ì¬ì‹œë„í•©ë‹ˆë‹¤."""
    for attempt in range(retries):
        try:
            urllib.request.urlretrieve(url, save_path)
            return True
        except Exception as e:
            print(f"ë‹¤ìš´ë¡œë“œ ì‹¤íŒ¨ (ì‹œë„ {attempt + 1}/{retries}): {e}")
            time.sleep(delay)
    return False


def sanitize_folder_name(name):
    """í´ë” ì´ë¦„ìœ¼ë¡œ ì‚¬ìš©í•  ìˆ˜ ì—†ëŠ” ë¬¸ì ì œê±°."""
    return re.sub(r'[\\/*?:"<>|]', "_", name)

def create_unique_folder_name(base_path, folder_name):
    """ì¤‘ë³µë˜ì§€ ì•ŠëŠ” ê³ ìœ í•œ í´ë” ì´ë¦„ ìƒì„±."""
    counter = 1
    unique_name = folder_name
    while os.path.exists(os.path.join(base_path, unique_name)):
        unique_name = f"{folder_name}({counter})"
        counter += 1
    return unique_name

def download_with_retry(url, path, retries=3):
    """ì´ë¯¸ì§€ë¥¼ ë‹¤ìš´ë¡œë“œí•˜ë©° ì¬ì‹œë„ ê¸°ëŠ¥ì„ í¬í•¨."""
    for attempt in range(retries):
        try:
            urlretrieve(url, path)
            return True
        except Exception as e:
            print(f"ë‹¤ìš´ë¡œë“œ ì‹¤íŒ¨ (ì¬ì‹œë„ {attempt + 1}/{retries}): {url} - {e}")
    return False


def create_folder_and_save_files(date, caption, img_list, obj):
    """teps4u í´ë”ë¥¼ ìƒì„±í•˜ê³  ìº¡ì…˜ ì €ì¥ ë° ì´ë¯¸ì§€ ë‹¤ìš´ë¡œë“œë¥¼ ì²˜ë¦¬í•©ë‹ˆë‹¤."""
    try:
        # í”„ë¡œê·¸ë¨ì´ ì‹¤í–‰ ì¤‘ì¸ í˜„ì¬ ë””ë ‰í„°ë¦¬ ê¸°ì¤€ìœ¼ë¡œ teps4u í´ë” ì„¤ì •
        base_path = os.getcwd()
        teps4u_path = os.path.join(base_path, "teps4u")
        os.makedirs(teps4u_path, exist_ok=True)

        # ë‚ ì§œë³„ ê³ ìœ  í´ë” ì´ë¦„ ìƒì„±
        sanitized_date = sanitize_folder_name(date)
        unique_folder_name = create_unique_folder_name(teps4u_path, sanitized_date)
        folder_path = os.path.join(teps4u_path, unique_folder_name)
        os.makedirs(folder_path, exist_ok=True)

        # objì˜ 'ë‚ ì§œ' ê°’ ìˆ˜ì • (í´ë” ì´ë¦„ë§Œ ì €ì¥)
        obj['ë‚ ì§œ'] = unique_folder_name

        # ìº¡ì…˜ ì €ì¥
        caption_file_path = os.path.join(folder_path, "caption.txt")
        try:
            if caption:  # caption ê°’ì´ ìˆëŠ” ê²½ìš°ì—ë§Œ íŒŒì¼ ìƒì„±
                with open(caption_file_path, "w", encoding="utf-8") as f:
                    f.write(caption[:10000])  # ìº¡ì…˜ ê¸¸ì´ ì œí•œ (ì˜ˆ: 10000ì)
            else:
                print("ìº¡ì…˜ì´ ë¹„ì–´ ìˆìŠµë‹ˆë‹¤. caption.txt íŒŒì¼ ìƒì„±ì„ ê±´ë„ˆëœë‹ˆë‹¤.")
        except Exception as e:
            print("ìº¡ì…˜ ì €ì¥ ì¤‘ ì˜¤ë¥˜ ë°œìƒ:", e)

        # ì´ë¯¸ì§€ ë‹¤ìš´ë¡œë“œ
        for idx, img_url in enumerate(img_list):
            img_path = os.path.join(folder_path, f"image_{idx + 1}.jpg")
            if not download_with_retry(img_url, img_path):
                print(f"ì´ë¯¸ì§€ ë‹¤ìš´ë¡œë“œ í¬ê¸°: {img_url}")

        print(f"íŒŒì¼ ì €ì¥ ì™„ë£Œ: {folder_path}")
    except Exception as e:
        print("íŒŒì¼ ì €ì¥ ì¤‘ ì˜¤ë¥˜ ë°œìƒ:", e)

